        public DataTable ConvertPro(List<ProductInfor> productInfors)
        {
            DataTable table = new DataTable();
            table.Columns.Add(new DataColumn("STT", typeof(System.Int32)));
            table.Columns.Add(new DataColumn("Mã code", typeof(System.String)));
            table.Columns.Add(new DataColumn("Thú nuôi", typeof(System.String)));
            table.Columns.Add(new DataColumn("Số lượng", typeof(System.Int32)));
            table.Columns.Add(new DataColumn("Trọng lượng trung bình", typeof(System.Double)));
