			table.Columns.Add(new DataColumn("Trọng lượng trung bình sản phẩm", typeof(System.Double)));
			table.Columns.Add(new DataColumn("Đơn vị đóng gói", typeof(System.String)));
			table.Columns.Add(new DataColumn("Kháng sinh", typeof(System.String)));
            //table.Columns.Add(new DataColumn("Đường tiêu thụ", typeof(System.String)));
            table.Columns.Add(new DataColumn("Ngày", typeof(System.String)));            
            table.Columns.Add(new DataColumn("Lượng kháng sinh/1 sản phẩm", typeof(System.Double)));
			table.Columns.Add(new DataColumn("Đơn vị lượng kháng sinh", typeof(System.String)));
			table.Columns.Add(new DataColumn("Số gói", typeof(System.Int32)));
            table.Columns.Add(new DataColumn("Người thực hiện", typeof(System.String)));
            table.Columns.Add(new DataColumn("Có đơn thuốc?", typeof(System.String)));

			table.Columns.Add(new DataColumn("Tên kháng sinh thứ nhất", typeof(System.String)));
			table.Columns.Add(new DataColumn("Lượng kháng sinh thứ nhất", typeof(System.Double)));
			table.Columns.Add(new DataColumn("Đơn vị kháng sinh thứ nhất", typeof(System.String)));
			table.Columns.Add(new DataColumn("Tên kháng sinh thứ hai", typeof(System.String)));
			table.Columns.Add(new DataColumn("Lượng kháng sinh thứ hai", typeof(System.Double)));
			table.Columns.Add(new DataColumn("Đơn vị kháng sinh thứ hai", typeof(System.String)));
			table.Columns.Add(new DataColumn("Tên kháng sinh thứ ba", typeof(System.String)));
			table.Columns.Add(new DataColumn("Lượng kháng sinh thứ ba", typeof(System.Double)));
			table.Columns.Add(new DataColumn("Đơn vị kháng sinh thứ ba", typeof(System.String)));

			table.AcceptChanges();
            //đổ data vào

            int count = 0;
            foreach (ProductInfor productinfor in productInfors)
            {
                DataRow row = table.NewRow();
				row["Đơn vị lượng sản phẩm"] = productinfor.QuantityUnit;
				row["Đơn vị trọng lượng trung bình vật nuôi"] = productinfor.Volume_AVG_Unit;
				row["Tên kháng sinh thứ nhất"] = productinfor.AB1;
				row["Lượng kháng sinh thứ nhất"] = productinfor.AB1_Amount;
				row["Đơn vị kháng sinh thứ nhất"] = productinfor.AB1_Unit;
				row["Tên kháng sinh thứ hai"] = productinfor.AB2;
				row["Lượng kháng sinh thứ hai"] = productinfor.AB2_Amount;
				row["Đơn vị kháng sinh thứ hai"] = productinfor.AB2_Unit;
				row["Tên kháng sinh thứ ba"] = productinfor.AB3;
				row["Lượng kháng sinh thứ ba"] = productinfor.AB3_Amount;
				row["Đơn vị kháng sinh thứ ba"] = productinfor.AB3_Unit;

				count++;
                row["STT"] = count;
                //row["Mã UV"] = candidate.Id.ToString();
                row["Mã code"] = productinfor.Code;
                row["Thú nuôi"] = productinfor.Pet;
                row["Số lượng"] = productinfor.AmountPet;
                row["Trọng lượng trung bình"] = productinfor.Volume_AVG;
                row["Sản phẩm"] = productinfor.Product;
                row["Lượng sản phẩm"] = productinfor.AmountProduct;
                row["Kháng sinh"] = productinfor.Antimicrobial;
				row["Đơn vị lượng kháng sinh"] = productinfor.AB_Unit;

				row["Trọng lượng trung bình sản phẩm"] = productinfor.VolumePackage;
				row["Đơn vị đóng gói"] = productinfor.UnitPackage;
				//var route = "";
				//if (productinfor.Route == 1)
				//{
				//    route = "Ăn";
				//}
				//else if (productinfor.Route == 2)
				//{
				//    route = "Uống";
				//}
				//else if (productinfor.Route == 3)
				//{
				//    route = "Tiêm";
				//}
				//row["Đường tiêu thụ"] = route;
				row["Ngày"] = DataConverter.DateTime2UI_Full(productinfor.CollectedDate);                
                row["Lượng kháng sinh/1 sản phẩm"] = productinfor.AB_Amount;
                row["Số gói"] = productinfor.VolumePackage;
                Ehr.Data.EhrDbContext db = new Data.EhrDbContext();
                var username = "";
                if (productinfor.UserId > 0)
                {
                    username = db.Users.Where(x => x.Id == productinfor.UserId).FirstOrDefault().FullName;
                }
                row["Người thực hiện"] = username;

                var IsPrescription = "";
                if (productinfor.IsPrescription == true)
                {
                    IsPrescription = "Có";
                }
                else
                {
                    IsPrescription = "Không";
                }
                row["Có đơn thuốc?"] = IsPrescription;
                table.Rows.Add(row);
            }
            table.AcceptChanges();
            return table;
        }

